template.value:
(function($) {

    $.secondaryBox = function(element, options) {

        var plugin = this;

        plugin.settings = {
            submit: true
        };

        var $element = $(element),
        ;

        plugin.init = function() {
            plugin.settings = $.extend({}, plugin.settings, options);

            var s = plugin.settings;


        };



        plugin.init();

        // return{
        //     create:createMember,
        //     get:getMemberDetails
        // }
    };

    $.fn.secondaryBox = function(options) {
        return this.each(function() {
            if (undefined === $(this).data('secondaryBox')) {
                var plugin = new $.secondaryBox(this, options);
                $(this).data('secondaryBox', plugin);
            }
        });
    };

    /**
     * DATA-* ATTRIBUTES API
     *
     * Use data-* attributes to enable/configure your sheeper components.
     */
    $(document).ready(function() {
        $('[data-init="secondary-box"]').secondaryBox({});
    });

})(jQuery);
